import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { UserService } from '../services/user.service';
import { User } from '../models/user';
import { NgForm } from '@angular/forms';
import {  Router } from '@angular/router';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent {

   user: User;

    constructor(private userService: UserService,private router: Router){
      this.user=new User();
    }

    ngOnInit(){

    }

    saveData(form:NgForm){
      if(form.valid){
        this.userService.AddUser(this.user).subscribe(res =>{
          if(res.status===201){
            this.router.navigate(['/']);
          }
        })

      }
    }

}
<h2>Create User</h2>
<hr />

<form #form="ngForm" (ngSubmit)="saveData(form)">
  <div class="form-group">
    <label>Name</label>
    <input
      type="text"
      name="name"
      #name="ngModel"
      [(ngModel)]="user.name"
      required
      class="form-control"
    />
    <div *ngIf="name.errors && form.submitted" class="text-danger">
      Please Enter Name
    </div>
  </div>
  <div class="form-group">
    <label>Address</label>
    <input
      type="text"
      name="address"
      #address="ngModel"
      [(ngModel)]="user.address"
      required
      class="form-control"
    />
    <div *ngIf="address.errors && form.submitted" class="text-danger">
      Please Enter Address
    </div>
  </div>
  <div class="form-group">
    <label>Contact</label>
    <input
      type="text"
      name="contact"
      #contact="ngModel"
      [(ngModel)]="user.contact"
      required
      class="form-control"
    />
    <div *ngIf="contact.errors && form.submitted" class="text-danger">
      Please Enter Contact
    </div>

    <div>
      <input type="submit" value="Save" class="btn btn-primary" />
    </div>
  </div>
</form>
